from time import sleep

import board
import digitalio


class WindMillController:
    def __init__(self, fan_pin, light_pin):
        self.fan_pin = digitalio.DigitalInOut(fan_pin)
        self.fan_pin.direction = digitalio.Direction.OUTPUT
        self.light_pin = digitalio.DigitalInOut(light_pin)
        self.light_pin.direction = digitalio.Direction.OUTPUT

    def set_speed(self, speed: int):
        # print(speed)
        if speed > 0:
            self.fan_pin.value = True
            self.light_pin.value = True
        else:
            self.fan_pin.value = False
            self.light_pin.value = False


windmill = WindMillController(board.G1, board.G2)

"""
docs:
The speed for the windmill is loaded every second from spinny_speed.msi, a Multidirectional Speed Input file
The speed for the windmill is loaded every second from spinny_speed.avi, a Air Velocity input
The power generated is outputted to zappy_zaps.exe a EXact Electron file
"""
speed = 0

while True:
    try:
        with open("data/buzzzt.exe", "rb") as f:
            data = f.read()
            num = int.from_bytes(data[0x0001152:0x0001156], "little")
    except OSError as e:
        print("Recreating")
        data = bytes.fromhex(
            ""
        )
        num = 0

    num += speed
    num &= 0xffffffff

    while True:
        try:
            with open("data/buzzzt.exe", "wb") as f:
                f.write(data[:0x0001152] + num.to_bytes(4, "little") + data[0x0001156:])
                break
        except OSError as e:
            if e.errno == 26:
                print("Waiting for output file to close")
                sleep(0.01)
                continue

    print(f"Power generated: {num}")

    try:
        with open("data/spinny_speed.avi") as f:
            speed = int(f.read().strip())
    except OSError:
        print("Could not read config")

    windmill.set_speed(speed)

    sleep(10)
